<?phpuse App\Http\Controllers\BookController;use App\Http\Controllers\UserController;use Illuminate\Support\Facades\Route;// BookRoute::get('books', [BookController::class, 'index']);Route::get('books/{id}', [BookController::class, 'show']);Route::get('books/search/{title}', [BookController::class, 'search']);Route::get('books/filter/{author}', [BookController::class, 'filter']);// UserRoute::post('register', [UserController::class, 'register']);Route::post('login', [UserController::class, 'login']);Route::middleware('auth:sanctum')->group(function () {    Route::post('/logout', [UserController::class, 'logout']);    Route::post('books', [BookController::class, 'store']);    Route::put('books/{id}', [BookController::class, 'update']);    Route::delete('books/{id}', [BookController::class, 'destroy']);});